mapper.select:
select

from house

    status = '未出租' and own_user_num != #{ownUserNum}

        and province = #{province}


        and city = #{city}


        and region = #{region}


        and desc_address like '%${descAddress}%'


        and area <= #{maxArea}


        and area >= #{minArea}


        and rent <= #{maxRent}


        and rent >= #{minRent}


        and rent_type = #{rentType}
